TestCase.Resource:

SELECT a FROM (
 SELECT a, RANK() OVER(PARTITION BY a ORDER BY SUM(b)) rk FROM x GROUP BY a
) WHERE rk <= 5


SELECT a FROM (
 SELECT a, RANK() OVER(PARTITION BY a ORDER BY SUM(b)) rk FROM x GROUP BY a
) WHERE rk <= 5 and rk >= 2


SELECT * FROM (
 SELECT a, b, rk, RANK() OVER(PARTITION BY a ORDER BY b) rk1 FROM (
   SELECT a, b, RANK() OVER(PARTITION BY a ORDER BY b) rk FROM x
 ) WHERE rk <= 5
) WHERE rk1 <= 5
